INSERT INTO `blog_bloganniversary` (`blog_anniversary_id`, `anniversary_date`, `anniversary_name`, `anniversary_desc`, `anniversary_website_operate`, `anniversary_website_show`, `status`, `create_by`, `update_by`, `create_time`, `update_time`, `remark`) VALUES
	(1, '5-12', '汶川大地震', '沉痛悼念汶川大地震遇难者', 1, 1, 0, 'superadmin', 'superadmin', '2018-12-04 17:06:02', '2018-12-13 17:16:54', ''),
	(10, '12-13', '国家公祭日', '今天是国家公祭日，我们以国祭之礼缅怀遇难同胞，是为了守护不能忘却的记忆，捍卫来之不易的和平。中国人民维护和平的决心坚定不移。中华民族为实现伟大复兴“中国梦”的意志坚如磐石。以史为鉴，在历史痛感中砥砺前行；唯有自强，方可傲然屹立于世界民族之林！', 1, 1, 0, 'superadmin', 'superadmin', '2018-12-13 08:07:14', '2018-12-13 08:07:14', ''),
	(11, '1-8', '周总理纪念日', '怀念周总理', 1, 2, 0, 'superadmin', 'superadmin', '2018-12-13 17:06:02', '2019-01-08 07:39:30', ''),
	(12, '2-5', '春节', '新春快乐，祝一帆风顺，二龙腾飞，三羊开泰，四季平安，五福临门，六六大顺，七星高照，八方来财，九九同心，十全十美！', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:09:48', '2018-12-13 17:09:48', ''),
	(13, '3-5', '雷锋纪念日', '向雷锋同志学习', 0, 2, 0, 'superadmin', 'superadmin', '2018-12-13 17:11:18', '2018-12-13 17:11:18', ''),
	(14, '3-8', '国际劳动妇女节', '妇女地位高，胖了，夸夸丰满;瘦了，赞赞苗条;爱动，那是活泼;矜持，那是文静;年轻，属于青春;年长，属于知性。三八妇女节，祝你快乐!', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:12:27', '2018-12-13 17:12:27', ''),
	(15, '5-1', '五一国际劳动节', '劳动光荣', 0, 2, 0, 'superadmin', 'superadmin', '2018-12-13 17:13:25', '2018-12-13 17:13:25', ''),
	(16, '4-5', '清明节', '踏青郊游、扫墓祭祖、礼敬祖先，亲近自然', 1, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:14:57', '2019-04-04 18:42:45', ''),
	(17, '7-1', '中国共产党诞生纪念日', '祝党生日快乐', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:18:58', '2018-12-13 17:18:58', ''),
	(18, '8-1', '中国人民解放军建军纪念日', '在中国共产党的坚强领导下，人民军队不断从胜利走向胜利，为民族独立和人民解放，为国家富强和人民幸福建立了彪炳史册的卓著功勋', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:19:50', '2018-12-13 17:19:50', ''),
	(19, '9-10', '教师节', '世界因为有你，才有了火样的热情。人生正因为有你的出现，才变得更加绚丽。教师节到了，祝愿老师节日快乐。', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:20:26', '2018-12-13 17:20:26', ''),
	(20, '10-1', '国庆节', '【国】富民强乐安闲，【庆】典庄严举国欢，【节】气馥郁笑开颜，【快】乐随行福弥漫，【乐】享人间美梦圆。国庆节快乐！', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:21:14', '2018-12-13 17:21:14', ''),
	(21, '9-3', '中国抗日战争胜利纪念日', '不会忘记在南京大屠杀和其他惨案中为中国难民提供帮助的外国朋友;不会忘记与中国军队并肩作战并为中国运送战略物资而冒险开辟驼峰航线的美国飞虎队;不会忘记不远万里前来中国救死扶伤的外国医护人员;不会忘记真实报导和宣传中国抗战业绩的外国记者;不会忘记为中国抗战胜利付出过心血的外国军事顾问及其他方面人士;我们更不会忘记在中国东北战场上英勇献身的苏军烈士!', 0, 1, 0, 'superadmin', 'superadmin', '2018-12-13 17:23:34', '2018-12-13 17:23:34', '');
