部\nparam3 = process.argv[4]; \nconsole.log(param1); \nconsole.log(param2); \nconsole.log(param3);\n\n//获取html \nfunction startGetHtml(url) {\n    var html = \'\';\n    //使用http发起请求      \n    http.get(url,function(res) {\n        //存储html\n       \n        //设置编码格式\n        res.setEncoding(\'utf-8\');\n        //监听data事件，获取数据\n        res.on(\'data\', function(chunk) {\n            html += chunk;\n        });\n        //监听end事件，如果都获取了则执行指定函数\n        res.on(\'end\',function() {\n            console.log(html);\n            cheerioHtml(html);\n        });\n    }).on(\'error\',function(err) {\n        console.log(err);\n    });\n}\nvar data;\n//解析\nfunction cheerioHtml(html){\n  var $ = cheerio.load(html, {decodeEntities: false});\n    //使用cheerio模块解析html\n    var msg = "";\n    msg += $(\'title\').html().trim();\n\n    data = "{\\"title\\":\\"" + msg + "\\"}"\n   //插入数据库\n    insertDataToDB(data)\n}\n\n//插入数据\nfunction insertDataToDB(data) {\n    data = [[param2, data, param3]];\n    connection.connect(function(err) {\n        if (err) {\n            console.log(\'连接失败\');\n        } else {\n            console.log(\'连接成功。\');\n            connection.query(\'insert into spider_data(task_info_id,json_data,task_flag) values ?\', [data],\n            function(err, data) {\n                if (err) {\n                    console.log(\'插入数据失败\')\n                } else {\n                    console.log(\'插入数据成功\');\n\n                    connection.end();\n                }\n            })\n\n        }\n    });\n}\n\n//开始爬虫\nfunction startSpider() {\n    var url = "http://www.baidu.com";\n    startGetHtml(url);\n}\n\n//启动\nstartSpider();', '', NULL, NULL, 0, 0, 0, NULL, 1, 1, 1, 0, 0, 240, 0, 1, 'superadmin', '2018-08-15 09:03:18', 'superadmin', '2018-12-10 09:05:17', '');
