mapper.select:
select * from
blog_blogcontent
where cid = #{cid}

select *
from
blog_blogcontent

	 and cid = #{cid}
	 and title = #{title}
	 and slug = #{slug}
	 and created = #{created}
	 and modified = #{modified}
	 and content = #{content}
	 and type = #{type}
	 and tags = #{tags}
	 and categories = #{categories}
	 and hits = #{hits}
	 and comments_num = #{commentsNum}
	 and allow_comment = #{allowComment}

		and allow_ping = #{allowPing}

	 and allow_feed = #{allowFeed}
	 and status = #{status}
	 and author = #{author}
	 and gtm_create = #{gtmCreate}
	 and gtm_modified = #{gtmModified}

		AND title like concat(concat('%', #{searchValue}), '%')

select cid, title,blog_type, slug, created,
modified,cover,images,
type, tags,
categories,
hits,
comments_num,like_num,browse_num,
allow_comment,
allow_ping,
allow_download,
allow_feed,
status, author,
gtm_create,
gtm_modified
,blog_column_name,show_id,
show_introduction,
introduction,
private_article
,scroll_recommended,special_recommended,recommended,article_editor,article_source,article_top
from
blog_blogcontent

	 and cid = #{cid}
	 and title = #{title}
	 and slug = #{slug}
	 and created = #{created}
	 and modified = #{modified}
	 and content = #{content}
	 and type = #{type}
	 and tags = #{tags}
	 and categories = #{categories}
	 and hits = #{hits}
	 and comments_num = #{commentsNum}
	 and allow_comment = #{allowComment}

		and allow_ping = #{allowPing}

	 and allow_feed = #{allowFeed}
	 and status = #{status}
	 and author = #{author}
	 and gtm_create = #{gtmCreate}
	 and gtm_modified = #{gtmModified}

		AND title like concat(concat('%', #{searchValue}), '%')

(select cid, title,blog_type, slug, created, modified,cover,images,
type, tags,
categories,
hits, comments_num,like_num,browse_num,
allow_comment,
allow_ping,
allow_download,
allow_feed,
status, author,
gtm_create,
gtm_modified
,blog_column_name,show_id,
show_introduction,
introduction,
private_article
,scroll_recommended,special_recommended,recommended,article_editor,article_source,article_top
from
blog_blogcontent

	 and cid = #{cid}
	 and title = #{title}
	 and slug = #{slug}
	 and created = #{created}
	 and modified = #{modified}
	 and content = #{content}
	 and type = #{type}
	 and tags = #{tags}
	 and categories = #{categories}
	 and hits = #{hits}
	 and comments_num = #{commentsNum}
	 and allow_comment = #{allowComment}
	 and allow_ping = #{allowPing}
	 and allow_feed = #{allowFeed}
	 and status = #{status}
	 and author = #{author}
	 and gtm_create = #{gtmCreate}
	 and gtm_modified = #{gtmModified}
	 and scroll_recommended = #{scrollRecommended}
	 and special_recommended = #{specialRecommended}
	 and recommended = #{recommended}
	and cid > 2 and article_top=0 and status=1 order by
	gtm_modified desc
	limit 999999

)
UNION ALL
(select cid, title,blog_type, slug, created,
modified,cover,images,
type, tags,
categories,
hits,
comments_num,like_num,browse_num,
allow_comment,
allow_ping,
allow_download,
allow_feed,
status, author,
gtm_create,
gtm_modified
,blog_column_name,show_id,
show_introduction,
introduction,
private_article
,scroll_recommended,special_recommended,recommended,article_editor,article_source,article_top
from
blog_blogcontent

	 and cid = #{cid}
	 and title = #{title}
	 and slug = #{slug}
	 and created = #{created}
	 and modified = #{modified}
	 and content = #{content}
	 and type = #{type}
	 and tags = #{tags}
	 and categories = #{categories}
	 and hits = #{hits}
	 and comments_num = #{commentsNum}
	 and allow_comment = #{allowComment}
	 and allow_ping = #{allowPing}
	 and allow_feed = #{allowFeed}
	 and status = #{status}
	 and author = #{author}
	 and gtm_create = #{gtmCreate}
	 and gtm_modified = #{gtmModified}
	 and scroll_recommended = #{scrollRecommended}
	 and special_recommended = #{specialRecommended}
	 and recommended = #{recommended}
	and cid > 2 and article_top <> 0 order by gtm_create desc limit
	999999

)

mapper.update:
update blog_blogcontent
title = #{title}, blog_type = #{blogType}, content = #{content}, content_md = #{contentMd}, cover = #{cover}, images = #{images}, type = #{type}, tags = #{tags}, allow_comment = #{allowComment}, allow_ping = #{allowPing}, allow_download = #{allowDownload}, allow_feed = #{allowFeed}, author = #{author}, status = #{status}, gtm_modified = #{gtmModified}, blog_column_name = #{blogColumnName}, show_id = #{showId}, blog_file_name = #{blogFileName}, show_introduction = #{showIntroduction}, introduction = #{introduction}, private_article = #{privateArticle}, article_editor = #{articleEditor}, article_top= #{articleTop}, article_source= #{articleSource}
where cid = #{cid}

update blog_blogcontent

	browse_num = browse_num+1

where cid = #{cid}

update blog_blogcontent

	like_num = like_num+1

where cid = #{cid}

update blog_blogcontent

	download_num = download_num+1

where cid = #{cid}

update blog_blogcontent

	comments_num = comments_num+1

where show_id = #{showId}

update blog_blogcontent

	comments_num = comments_num-1

where show_id = #{showId}

update blog_blogcontent
article_top = #{1}, gtm_modified=sysdate()
where cid = #{0}
