mapper.select:
select ct.*,ct2.tool_back_id,ct2.tool_type,ct2.src_download_status,ct2.execexe_download_status,ct2.setupexe_download_status from commontool_toollist ct left join commontool_toolmanage ct2 on ct.tool_name=ct2.tool_name
and ct.create_by = #{createBy}
AND (ct.tool_nick_name like concat(concat('%', #{searchValue}), '%') OR ct.tool_name like concat(concat('%', #{searchValue}), '%'))

mapper.update:
update commontool_toollist
tool_nick_name = #{toolNickName}, tool_name = #{toolName}, tool_instruction = #{toolInstruction}, update_by = #{updateBy}, remark = #{remark}, update_time =sysdate()
where tool_list_id = #{toolListId}
