mapper.select:
select baseset_id, theme_name, loginbg_name,loginbg_file_name,loginbg_type, home_introduction, spider_java_package_prefix, spider_java_code_example, spider_python_code_example, spider_javascript_code_example, download_file_name_prefix, spider_website_manual, spider_use_manual,spider_website_manual_type,spider_use_manual_type, show_music_tool_status,music_info,status, use_status, create_by, create_time, update_by, update_time, remark from tool_baseset
and baseset_id = #{basesetId} and theme_name = #{themeName} and spider_java_package_prefix = #{spiderJavaPackagePrefix}
and download_file_name_prefix = #{downloadFileNamePrefix}
AND theme_name like concat(concat('%', #{searchValue}), '%')

mapper.update:
update tool_baseset
loginbg_name = #{loginbgName}, loginbg_file_name = #{loginbgFileName}, loginbg_type = #{loginbgType}, update_by = #{updateBy}, update_time =sysdate()
where baseset_id = #{basesetId}

update tool_baseset
music_info=#{musicInfo}, update_by = #{updateBy}, update_time =sysdate()
where baseset_id = #{basesetId}

update tool_baseset
use_status = 0, update_time =sysdate()
where baseset_id = #{basesetId}
