mapper.select:
select straightlinkmanage_id, straightlink_file_name,
straightlink_file_uuid_name, straightlink_file_type,
straightlink_file_extension_name, straightlink_file_size,
straightlink_file_url_key, straightlink_file_url,
straightlink_file_down_num, status, use_status, create_by,
create_time, update_by, update_time, remark from
tool_straightlinkmanage

	 and straightlinkmanage_id = #{straightlinkmanageId}
	 and straightlink_file_name = #{straightlinkFileName}

	 and straightlink_file_uuid_name = #{straightlinkFileUuidName}

	 and straightlink_file_type = #{straightlinkFileType}

	 and straightlink_file_extension_name =
		#{straightlinkFileExtensionName}

	 and straightlink_file_size = #{straightlinkFileSize}

	 and straightlink_file_url_key =
		#{straightlinkFileUrlKey}

	 and straightlink_file_url = #{straightlinkFileUrl}
	 and straightlink_file_down_num = #{straightlinkFileDownNum}

	 and status = #{status}
	 and use_status = #{useStatus}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

		AND straightlink_file_name like concat(concat('%',
		#{searchValue}), '%')

mapper.update:
update tool_straightlinkmanage
straightlink_file_name = #{straightlinkFileName}, straightlink_file_uuid_name = #{straightlinkFileUuidName}, straightlink_file_type = #{straightlinkFileType}, straightlink_file_extension_name = #{straightlinkFileExtensionName}, straightlink_file_size = #{straightlinkFileSize}, straightlink_file_url_key = #{straightlinkFileUrlKey}, straightlink_file_url = #{straightlinkFileUrl}, status = #{status}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where straightlinkmanage_id = #{straightlinkmanageId}
